import React, { Component } from 'react';
import { Tooltip } from 'react-tooltip';
import imageAbout from './imgAbout1.png';
import imageExpert1 from './imgExpert1.png';
import imageExpert2 from './imgExpert2.png';
import imageExpert3 from './imgExpert3.png';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";
import Carousel from 'react-bootstrap/Carousel';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import FloatingLabel from 'react-bootstrap/FloatingLabel';
import { useState } from 'react';

function About() {

        const [index, setIndex] = useState(0);
        const [isShown, setIsShown] = useState(false);

        /*const handlebtnclickSub = () => {
            const sub = window.confirm('Once you click Submit, you will not be able to edit your review. Are you sure you want to submit?');
            if (sub == true) {
              window.location.reload();
            }
            else{

            }
          };
        */
        const handleSelect = (selectedIndex) => {
            setIndex(selectedIndex);
        };

        /*const [name, setName] = useState("");
        const nameChangeHandler = (e) => {
            setName(e.target.value)
        };*/

        const [val, setVal] = useState('');
        const [confirm, setConfirm] = useState(false);
        const [sauverVal, setSauverVal] = useState('');

        const handleChangement = (event) => {
            setVal(event.target.value);
        };

        const handleSoumettre = () => {
            setConfirm(true);
        };

        const handleAnnuler = () => {
            setConfirm(false);
        };

        const handleConfirmation = () => {
            setSauverVal(val);
            setConfirm(false);
        }

        return (
            <div>
                <h1 className='titlePages'>ABOUT US</h1>

                <div className='aboutInfo'>
                    <div className='aboutInfo1'>
                        <h2>Dulce beauty clinic</h2>
                        <p>At our clinic, we have a team of qualified experts who have been in the beauty industry for several years.</p>
                        <p>The Dulce Beauty clinic offers you a wide choice of high-end services.</p>
                        <p>We prioritize the comfort and the satisfaction of our customers.</p>
                    </div>
                    <div className='aboutImg1'>
                        <img src={imageAbout} alt='imgAbout' className='img-fluid'/>
                    </div>
                </div>

                <h1 className='titlePages2' style={{marginTop: '250px', marginBottom: '100px',}}>OUR EXPERTS</h1>

                <div>
                    <Carousel className='experts'>
                        <Carousel.Item>
                            <div className='expertsImg'>
                                <img src={imageExpert1} alt='imgAbout' className='img-fluid'/>
                            </div>
                            <h2>John Doe</h2>
                            <h3>Facial expert</h3>
                            <Link 
                                className="btn btn-outline-light btn-lg"
                                role="button"
                                to="/services"
                                > 
                                Services
                            </Link>
                        </Carousel.Item>
                        <Carousel.Item>
                            <div className='expertsImg'>
                                <img src={imageExpert2} alt='imgAbout' className='img-fluid'/>
                            </div>
                            <h2>Jane Doe</h2>
                            <h3>Body Treatement Expert</h3>
                            <Link 
                                className="btn btn-outline-light btn-lg"
                                role="button"
                                to="/services"
                                > 
                                Services
                            </Link>
                        </Carousel.Item>
                        <Carousel.Item>
                            <div className='expertsImg'>
                                <img src={imageExpert3} alt='imgAbout' className='img-fluid'/>
                            </div>
                            <h2>Sofia Hayes</h2>
                            <h3>Esthetic expert</h3>
                            <Link 
                                className="btn btn-outline-light btn-lg"
                                role="button"
                                to="/services"
                                > 
                                Services
                            </Link>
                        </Carousel.Item>
                    </Carousel>
                </div>

                <div className='showReviews'>
                    <h1 className='titlePages3'>REVIEWS</h1>
                    <Carousel className='CarouselReviews'>
                        <Carousel.Item>
                            <h3>Love this clinic!</h3>
                            <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
                        </Carousel.Item>
                        <Carousel.Item>
                            <h3>Definitly going back here</h3>
                            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
                        </Carousel.Item>
                        <Carousel.Item>
                            <h3>Good experience</h3>
                            <p>
                                Praesent commodo cursus magna, vel scelerisque nisl consectetur.
                            </p>
                        </Carousel.Item>
                        <Carousel.Item>
                            <h3>Good experience</h3>
                            <p>
                                Praesent commodo cursus magna, vel scelerisque nisl consectetur.
                            </p>
                        </Carousel.Item>
                        <Carousel.Item>
                            <h3>Good experience</h3>
                            <p>
                                Praesent commodo cursus magna, vel scelerisque nisl consectetur.
                            </p>
                        </Carousel.Item>
                    </Carousel>
                </div>
                <div>
                    <h1 className='titlePages4' style={{marginTop: '150px'}}>ADD A REVIEW</h1>
                    <Form className='addingReview'>
                        <Form.Group className="mb-3" controlId="formName">
                            <div className='addingReview2'style={{display: 'flex',}}>
                                <Form.Label style={{marginRight: '20px' }}>Name</Form.Label>
                                <Form.Control type="text" placeholder="" style={{width: '530px'}}/>
                            </div>
                        </Form.Group>
                        <FloatingLabel className='addingReview2' controlId="floatingTextarea2" label="Comments">
                            <Form.Control
                            as="textarea"
                            placeholder="Leave a comment here"
                            style={{height: '200px', width: '600px',}}
                            type="text"
                            onChange={handleChangement}
                            value={val}
                            />
                        </FloatingLabel>
                        <Button 
                            style={{marginTop: '40px', marginRight: '100px'}}
                            variant="primary"
                            type="submit"
                            onClick={handleSoumettre}
                        >
                            Submit
                        </Button>
                        {confirm && (
                            <div>
                                <p>Do you want to cancel?</p>
                                <button onClick={handleAnnuler}>No</button>
                                <button onClick={handleConfirmation}>No</button>
                            </div>
                        )

                        }
                    </Form>
                </div>
            </div>
        )
}
export default About;
