        { type: 'Body Treatment', price: '100', time: '50'},
        { type: 'Facial Treatment', price: '175', time: '60'},
        { type: 'Facial Treatment', price: '130', time: '75'},
        { type: 'Facial Treatment', price: '100', time: '40'},
        { type: 'Esthetic', price: '175', time: '75'},
        { type: 'Esthetic', price: '130', time: '75'},
        { type: 'Esthetic', price: '100', time: '60'}
    ];

    const filteredData = data.filter((item) => {
