import { Form } from 'react-bootstrap';

export const FormItem = ({ item, onChange }) => {
    switch (item.type) {
        case 'text':
        return(
            <>
                <Form.Label>{item.label}</Form.Label>
                <Form.Control
                    type="text"
                    id={item.label}
                    onChange={(e) => onChange(e.target.value, )}
                />
            </>
        )
        break;
        case 'select':
        return(
            <div className='mt-2'>
                <Form.Label>{item.label}</Form.Label>
                <Form.Select aria-label={item.label}>
                    <option>Open this select list</option>
                    {
                        item.options.map((opt, index) => {
                            return (
                                <option value={opt}>{opt}</option>
                            )
                        })
                    }
                </Form.Select>
            </div>
        )
        break;
        case 'information':
        return(
            <p>{item.label}</p>
        )
        break;
    }

    return[
        <></>
    ]
}
