import React, { Component } from 'react';
import { MultiStepProgressBar } from "./MultiStepProgressBar";
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import { useState } from 'react';
import { PersonalInfo } from './PersonalInfo';
import { MultiStepForm } from './MultiStepForm';

function Book(){
    const[index, setIndex] = useState(1);
    const totalPagesCount = PersonalInfo.length;
    const [pagesAnswers, setPagesAnswers] = useState({});

    const onPagesAnswerUpdate = (step, answersObj) => {
        setPagesAnswers({...pagesAnswers, [step]: answersObj});
    } 

    const prevButton = () => {
        if (index > 1){
            setIndex(prevIndex => prevIndex - 1);
        }
    }

    const nextButton = () => {
        if (index < 3){
            setIndex(prevIndex => prevIndex + 1);
        }
    }
    return (
        <div>
            <Container className='h-100'>
                <Row className='m-5'>
                    <Col className='align-self-center'>
                        <MultiStepProgressBar step={index}/>
                    </Col>
                </Row>
                <Row className='d-flex justify-content-between'
                style={{marginTop: '50px',}}>
                    <div className='titreBooking'>
                        <h1 style={{display: 'flex', marginBottom: '30px'}}>Welcome!</h1>
                        <h2 style={{display: 'flex', marginLeft: '15px'}}>Enter your personal information</h2>
                    </div>
                    <MultiStepForm step={index} list = {PersonalInfo}/>
                    <Button onClick={prevButton} disabled = {index === 1}>Previous</Button>
                    <Button onClick={nextButton}>{index === totalPagesCount? 'Submit' : 'Next'}</Button>
                </Row>
            </Container>
        </div>
    )

}
export default Book;
